import React, { useState } from 'react';
import { Container, Form, Row, Col } from 'react-bootstrap';
import PlayerCard from '../components/cards/PlayerCard';
import MatchCard from '../components/cards/MatchCard';
import api from '../services/api';

const Search = () => {
  const [q, setQ] = useState('');
  const [players, setPlayers] = useState([]);
  const [matches, setMatches] = useState([]);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!q.trim()) return;
    try {
      const [pRes, mRes] = await Promise.all([
        api.get(`/search/players?q=${encodeURIComponent(q)}`),
        api.get(`/search/matches?q=${encodeURIComponent(q)}`)
      ]);
      setPlayers(pRes.data || []);
      setMatches(mRes.data || []);
    } catch (err) {
      console.error('Error searching', err);
      alert('Error en la búsqueda (ver consola).');
    }
  };

  return (
    <Container className="py-4">
      <h3>Buscar</h3>
      <Form onSubmit={handleSearch} className="mb-3">
        <Row>
          <Col md={9}>
            <Form.Control placeholder="Buscar jugador, equipo o partido..." value={q} onChange={(e) => setQ(e.target.value)} />
          </Col>
          <Col md={3}>
            <Form.Control type="submit" value="Buscar" as="button" className="btn btn-primary w-100" onClick={handleSearch} />
          </Col>
        </Row>
      </Form>

      <Row>
        <Col md={6}>
          <h5>Jugadores</h5>
          {players.map(p => <PlayerCard key={p.id || p.name} player={p} />)}
        </Col>
        <Col md={6}>
          <h5>Partidos</h5>
          {matches.map(m => <MatchCard key={m.id || (m.home+m.away)} match={m} />)}
        </Col>
      </Row>
    </Container>
  );
};

export default Search;
EOF
