import React, { createContext, useState, useEffect } from 'react';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(() => localStorage.getItem('fs_token'));

  useEffect(() => {
    if (token) {
      // en el backend validar token; aquí simulamos decodificación mínima
      setUser({ name: 'Usuario FutStats', email: 'usuario@futstats.test' });
    } else {
      setUser(null);
    }
  }, [token]);

  const login = (tokenValue) => {
    localStorage.setItem('fs_token', tokenValue);
    setToken(tokenValue);
  };

  const logout = () => {
    localStorage.removeItem('fs_token');
    setToken(null);
  };

  return (
    <AuthContext.Provider value={{ user, token, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
