import React, { useEffect, useState, useContext } from 'react';
import { Container, ListGroup, Button } from 'react-bootstrap';
import CommentBox from '../components/CommentBox';
import api, { deleteOpinion } from '../services/api';
import { AuthContext } from '../context/AuthContext';

const Comments = () => {
  const [opinions, setOpinions] = useState([]);
  const { token } = useContext(AuthContext);

  const fetchOpinions = async () => {
    const res = await api.get('/opinions');
    setOpinions(res.data || []);
  };

  useEffect(() => { fetchOpinions(); }, []);

  const handleDelete = async (id) => {
    if (!confirm('¿Eliminar esta opinión?')) return;
    try {
      await deleteOpinion(id, token);
      setOpinions(opinions.filter(o => o._id !== id));
    } catch (err) {
      console.error(err);
      alert('Error borrando opinión');
    }
  };

  return (
    <Container className="py-4">
      <h3>Opiniones</h3>
      <CommentBox onPosted={(newOp) => setOpinions([newOp, ...opinions])} />
      <ListGroup className="mt-3">
        {opinions.map(op => (
          <ListGroup.Item key={op._id || op.id} className="d-flex justify-content-between align-items-start">
            <div>
              <div className="fw-bold">{op.authorName || 'Anónimo'}</div>
              <small className="text-muted">{op.text}</small>
            </div>
            <div>
              {token && <Button size="sm" variant="danger" onClick={() => handleDelete(op._id || op.id)}>Eliminar</Button>}
            </div>
          </ListGroup.Item>
        ))}
      </ListGroup>
    </Container>
  );
};

export default Comments;
EOF
