import React, { useState, useContext } from 'react';
import { Button, Form } from 'react-bootstrap';
import { AuthContext } from '../context/AuthContext';
import { postOpinion } from '../services/api';

const CommentBox = ({ onPosted }) => {
  const [text, setText] = useState('');
  const { token } = useContext(AuthContext);

  const submit = async (e) => {
    e.preventDefault();
    if (!text.trim()) return;
    try {
      const res = await postOpinion({ text }, token);
      setText('');
      if (onPosted) onPosted(res.data);
    } catch (err) {
      console.error('Error posting opinion', err);
      alert('No se pudo publicar la opinión (ver consola).');
    }
  };

  return (
    <Form onSubmit={submit}>
      <Form.Group>
        <Form.Control
          as="textarea"
          rows={3}
          placeholder={token ? "¿Qué opinas del partido?" : "Inicia sesión para comentar..."}
          value={text}
          onChange={(e) => setText(e.target.value)}
          disabled={!token}
        />
      </Form.Group>
      <div className="d-flex justify-content-end mt-2">
        <Button type="submit" disabled={!token}>Publicar</Button>
      </div>
    </Form>
  );
};

export default CommentBox;
EOF
